import React, { useEffect, useState } from 'react';
import './Stage2.css';

function Stage2() {
    const queryParams = new URLSearchParams(window.location.search);
    const players = parseInt(queryParams.get('players'), 10) || 1;
    const mode = queryParams.get('mode');

    // Initialize an array to store input field values
    const [playerNames, setPlayerNames] = useState(Array(players).fill(''));

    // Handle input changes and update the playerNames array
    const handleInputChange = (event, index) => {
        const updatedPlayerNames = [...playerNames];
        updatedPlayerNames[index] = event.target.value;
        setPlayerNames(updatedPlayerNames);
    };

    // Render input fields based on the number of players
    const playerInputs = [];

    function redirect() {
        const queryParams = `?names=${names}`;
        window.location.href = '/stage3' + queryParams;
    }
    for (let i = 0; i < players; i++) {
        playerInputs.push(
            <div key={i} className="form__group field">
                <input
                    type="input"
                    className="form__field"
                    placeholder={`Player ${i + 1} Name`}
                    name={`player${i}`}
                    id={`player${i}`}
                    required
                    value={playerNames[i]}
                    onChange={(event) => handleInputChange(event, i)}
                />
                <label htmlFor={`player${i}`} className="form__label">{`Player ${i + 1} Name`}</label>
            </div>
        );
    }

    return (
        <div className='stage1'>
            <div className='textfield2'>
                <div className='stage2'>
                    {playerInputs}
                </div>
                <button className='buttonletsplay'  style={{ marginTop: '20px' }} onClick={redirect}>Let's start</button>
            </div>
        </div>
    );
}

export default Stage2;
