  const league = queryParams.get('league');
  const rating = queryParams.get('rating');
  let result = [];
  let teams = [];

  useEffect(() => {
    if (mode === 'singles') {
      if (league === 'La liga') {
        const selectedLeague = data.leagues.find((l) => l.name === 'La liga');
        if (selectedLeague) {
          const teamsSelected = selectedLeague.teams.filter((team) => team.rating === rating);
          for (let i = 0; i < players; i++) {
            let index = Math.floor(Math.random() * teamsSelected.length);
            while (!teams.includes(teamsSelected[index])) {
